Ajax.jsonp("http://api.flickr.com/services/feeds/photos_public.gne?tags=cat&tagmode=any&format=json&jsoncallback=?", function(data){
Object.each(data.items, function(item, i){
  Dom.query("<img/>").attr("src", item.media.m).appendTo("#images");
  if ( i == 3 ) return false;
  });
});

$.json("test.js", { name: "John", time: "2pm" }, function(json){
alert("JSON Data: " + json.users[3].name);
});

Object.each( [0,1,2], function(i, n){
alert( "Item #" + i + ": " + n );
});

Object.each( { name: "John", lang: "JS" }, function(i, n){
alert( "Name: " + i + ", Value: " + n );
});

function stub() {
}
var objs = [
function () {},
{ x:15, y:20 },
null,
stub,
"function"
];
Control.each(objs, function (i) {
var isFunc = Function.isFunction(objs[i]);
Dom.query("span").setText(isFunc);
});
